const fileInput=document.querySelector("#file-input"),extensionButton=document.querySelector("#extensions-button"),convertButton=document.querySelector("#convert-button"),convertImage=async e=>{const t=fileInput.files[0],n=new Image;n.src=URL.createObjectURL(t),await new Promise((e=>n.onload=e));const a=document.createElement("canvas");a.width=n.width,a.height=n.height;a.getContext("2d").drawImage(n,0,0);const c=a.toDataURL(`image/${e}`),o=document.createElement("a");o.href=c,o.download=`converted.${e}`,o.click()};convertButton.addEventListener("click",(async()=>{if(0!==fileInput.files.length)switch(extensionButton.innerHTML){case"JPG":await convertImage("jpg");break;case"PNG":await convertImage("png");break;default:alert("Please select an extension.")}else alert("Please select a file.")}));